func main() {

	var bookName string
	fmt.Printf("请输入查找书名：")
	bookName = "三体"
	input := bufio.NewScanner(os.Stdin)

	// 逐行扫描
	if input.Scan() {
		bookName = input.Text()
	}

	searchBookByName(bookName)

}

type BookList struct {
	BookId     string
	Name       string
	Author     string
	Publisher  string
	Isbn       string
	Pcount     int
	Ecount     int
	SearchCode string
	Image      string
}

func searchBookByName(bookName string) {

	var htmlContent string

	options := []chromedp.ExecAllocatorOption{
		chromedp.Flag("headless", true),
		chromedp.Flag("blink-settings", "imagesEnabled=false"),
		chromedp.UserAgent(`Mozilla/5.0 (Windows NT 6.3; Win64; x64) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/73.0.3683.103 Safari/537.36`),
	}
	options = append(chromedp.DefaultExecAllocatorOptions[:], options...)

	c, _ := chromedp.NewExecAllocator(context.Background(), options...)

	chromeCtx, cancel := chromedp.NewContext(c, chromedp.WithLogf(log.Printf))
	defer cancel()
	//	chromedp.Run(chromeCtx, make([]chromedp.Action, 0, 1)...)
	// 给每个页面的爬取设置超时时间
	ctx, cancel := context.WithTimeout(chromeCtx, 20*time.Second)
	defer cancel()

	err := chromedp.Run(ctx,
		chromedp.Navigate("https://findcumt.libsp.com/#/Home"),
		chromedp.WaitVisible(`#app > div > div > div:nth-child(2) > div > div > div > div > div.searchBg___2oNkd > div > div.searchContent___1ZkDa > div > div > div > div > ul > li > div > input`),
		chromedp.SendKeys(`#app > div > div > div:nth-child(2) > div > div > div > div > div.searchBg___2oNkd > div > div.searchContent___1ZkDa > div > div > div > div > ul > li > div > input`, bookName),
		chromedp.Sleep(time.Second),
		chromedp.Click(`#app > div > div > div:nth-child(2) > div > div > div > div > div.searchBg___2oNkd > div > div.searchContent___1ZkDa > div > i`),
		chromedp.WaitVisible(`#scrollContent > div.mainBody___gqqQr > div > div.searchList___17MNz > div.ant-list.ant-list-something-after-last-item > div > div.ant-spin-nested-loading`),
		chromedp.OuterHTML(`#scrollContent > div.mainBody___gqqQr > div > div.searchList___17MNz > div.ant-list.ant-list-something-after-last-item > div > div.ant-spin-nested-loading`, &htmlContent),
	)

	ctx.Done()

	if err != nil {
		log.Fatal(err)
	}

	bookInfo(htmlContent)

}

